const assert = require('assert');
const countStudents = require('./2-read_file');

describe('countStudents', () => {
  it('should throw error for non-existent file', () => {
    assert.throws(
      () => countStudents('nope.csv'),
      { message: 'Cannot load the database' }
    );
  });

  it('should correctly count students', () => {
    // This will test console output
    const originalLog = console.log;
    const outputs = [];
    console.log = (msg) => outputs.push(msg);

    countStudents('database.csv');

    console.log = originalLog;

    assert.equal(outputs[0], 'Number of students: 10');
    assert.ok(outputs[1].includes('Number of students in CS: 6'));
    assert.ok(outputs[2].includes('Number of students in SWE: 4'));
  });
});

